INSERT INTO `Ramon_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2016-04-24 23:56:31', '2016-04-24 23:56:31', 'Hello,\r\n\r\nThank you for supporting the San Jose Fiesta restaurant for so long. Feel free to look around our website.\r\n\r\nGracias Amigo!\r\n\r\nRamon Arellano\r\n\r\n&nbsp;\r\n\r\nHere''s our most up to date information:\r\n<h2>Location and\r\nContact information:</h2>\r\n<h3>99 Wallace Drive,\r\nNashville, TN 37211\r\n(615) 891-3007</h3>\r\n<h2>Open 4pm-3am, 7 days a week.</h2>\r\n&nbsp;\r\n\r\n<img class="alignnone size-medium wp-image-28" src="http://localhost:8888/wordpress/wp-content/uploads/2016/04/DSC_4627-300x200.jpg" alt="DSC_4627" width="300" height="200" />', 'Welcome to our website!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2016-05-21 02:06:48', '2016-05-21 02:06:48', '', 0, 'http://localhost:8888/wordpress/?p=1', 0, 'post', '', 0),
(2, 1, '2016-04-24 23:56:31', '2016-04-24 23:56:31', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://localhost:8888/wordpress/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2016-04-24 23:56:31', '2016-04-24 23:56:31', '', 0, 'http://localhost:8888/wordpress/?page_id=2', 0, 'page', '', 0),
(4, 1, '2016-04-25 00:04:57', '2016-04-25 00:04:57', 'We have proudly been one of the only restaurants to have live boxing in the United States.\r\n\r\nPlease check the <a href="/wordpress/events/" target="_blank">events</a> calendar for upcoming matches.', 'Come See Our Boxing', '', 'publish', 'open', 'open', '', 'come-see-our-boxing', '', '', '2016-05-21 01:18:05', '2016-05-21 01:18:05', '', 0, 'http://localhost:8888/wordpress/?p=4', 0, 'post', '', 0),
(5, 1, '2016-04-25 00:04:57', '2016-04-25 00:04:57', 'We will be having boxing on the 24th of August. For more information visit our twitter page.', 'Come See Our Boxing', '', 'inherit', 'closed', 'closed', '', '4-revision-v1', '', '', '2016-04-25 00:04:57', '2016-04-25 00:04:57', '', 4, 'http://localhost:8888/wordpress/2016/04/25/4-revision-v1/', 0, 'revision', '', 0),
(6, 1, '2016-04-25 00:05:34', '2016-04-25 00:05:34', 'Our Specials run 4-8pm Monday- Friday.\r\n\r\n7.99\r\n\r\nMonday: Burrito\r\nTuesday: Nachos\r\nWednesday: Flautas\r\nThursday: Chimichanga\r\nFriday: Quesadilla or 2 Tacos (Beans and Rice)', 'Our Daily Specials', '', 'publish', 'open', 'open', '', 'check-out-our-specials', '', '', '2016-05-21 00:57:56', '2016-05-21 00:57:56', '', 0, 'http://localhost:8888/wordpress/?p=6', 0, 'post', '', 0),
(7, 1, '2016-04-25 00:05:34', '2016-04-25 00:05:34', 'Our specials include Enchiladas and Burritos.', 'Check out our Specials', '', 'inherit', 'closed', 'closed', '', '6-revision-v1', '', '', '2016-04-25 00:05:34', '2016-04-25 00:05:34', '', 6, 'http://localhost:8888/wordpress/2016/04/25/6-revision-v1/', 0, 'revision', '', 0),
